import React from "react";
import Navbar from './components/Navbar'
import Jumbotron from './components/Jumbotron'
import Container from './components/Container'
import Card from './components/Card'
import characters from "./characters.json";
import './styles/App.css';
//import Button from "./components/Button";

class App extends React.Component {
  // Setting this.state.characters to the characters json array
  state = {
    cards: characters,
    clickedArray: [],
    topScore: 0,
    score: 0,
    message: "",
    shakeit: "false"
  };
  clickPicture = id => {
    // Arrange the pictures in a random manner
    const shuffledArray = this.shuffleArray(characters);
    this.setState({cards: shuffledArray});
    // if clicked an image already clicked set this.state.score = 0; empty clickeadArray, end of if block
    if (this.state.clickedArray.includes(id)) {
      this.setState({ score: 0, clickedArray: [], message: "Incorrect!! Game Over ☹️ Click an image to start again!", shakeit: "true"});
    }
    else {
      this.setState({
        clickedArray: this.state.clickedArray.concat([id]),
        score: this.state.score + 1,
        message: "Correct!! 🙂",
        shakeit: "false"
      });
    }
    // set topscore = score if score>topscore.
    if (this.state.score > this.state.topScore) {
      this.setState({ topScore: this.state.score });
    }
    // shake the wrapper if shakeit is set to true
  }
  shuffleArray = (picturesArray) => {
      for (let i = picturesArray.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [picturesArray[i], picturesArray[j]] = [picturesArray[j], picturesArray[i]];
      }
      return picturesArray;
  }
  render () {
    return (
      <div>
        <Navbar/>
        <Jumbotron>
        </Jumbotron>

        <Container>

        {this.state.cards.map(character =>{
          return (

              <Card
              onClick={this.clickPicture}
              id={character.id}
              key={character.id}
              name={character.name}
              image={character.image}
              />


          )
        })}

        </Container>

      </div>
    )
  }
}


export default App;
