import React from "react";
import Navbar from './components/Navbar'
import Jumbotron from './components/Jumbotron'
import Container from './components/Container'
import Card from './components/Card'
import characters from "./characters.json";
import './styles/App.css';
import Button from "./components/Button";

class App extends React.Component {
  // Setting this.state.characters to the characters json array
  state = {
    characters,
		score: 0,
		highscore: 0,
    status: "",
    cards: characters
  };

  handleShuffleArr(arr) {
		let i = characters.length - 1;
		for (; i > 0; i--) {
			const j = Math.floor(Math.random() * (i + 1));
			const temp = characters[i];
			characters[i] = characters[j];
			characters[j] = temp;
		}
    return this.setState({
      cards: characters
    });
  }

  render () {
    return (
      <div>
        <Navbar/>
        <Jumbotron>
        </Jumbotron>

        <Container>

        {this.state.cards.map(character =>{
          return (
           <Button onClick={() => this.handleShuffleArr(this.state.characters)} key={character.id}>
             <Card

              id={character.id}
              key={character.id}
              name={character.name}
              image={character.image}
              />
           </Button>



          )
        })}
         <button onClick={() => this.handleShuffleArr(this.state.cards)}>shuffle</button>
        </Container>

      </div>
    )
  }
}


export default App;
