import React from "react";
import Navbar from './components/Navbar'
import Jumbotron from './components/Jumbotron'
import Container from './components/Container'
import Card from './components/Card'
import characters from "./characters.json";
import './styles/App.css';
import Button from "./components/Button";

class App extends React.Component {
  // Setting this.state.characters to the characters json array
  state = {
    characters,
		score: 0,
		highscore: 0,
    status: "",
    selected: [],
    cards: characters
  };

  cardClicked = event => {
    const currentCard = event.target.id

    const alreadyClicked = this.state.clicked.indexOf(currentCard) > -1;

    this.handleShuffleArr(this.state.characters);

  }




  shuffleCards = () => {
    this.state.cards.sort( (a,b) => {return 0.5 - Math.random()});
  };

  handleShuffleArr(arr) {
		let i = arr.length - 1;
		for (; i > 0; i--) {
			const j = Math.floor(Math.random() * (i + 1));
			const temp = arr[i];
			arr[i] = arr[j];
			arr[j] = temp;
		}
    return this.setState({
      cards: arr
    });
  }

  render () {
    return (
      <div>
        <Navbar/>
        <Jumbotron>
        </Jumbotron>

        <Container>

        {this.state.characters.map(character =>{
          return (

             <Card
              onClick={this.cardClicked}
              id={character.id}
              key={character.id}
              name={character.name}
              image={character.image}
              />




          )
        })}
         <Button onClick={() => this.handleShuffleArr(this.state.cards)}>shuffle</Button>
        </Container>

      </div>
    )
  }
}


export default App;
